const float M_PI = 3.14159265358979323846264338327950288;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 res = iResolution.xy,
    uv = (fragCoord*2.0-res ) / res.y;
	uv*=.7;
    //uv = fragCoord;

    //uv -= 0.5;


    vec2 u = uv * 0.5;
    //u = fract(u * iTime);
    float uvX = length(u);
    float ball = fract(uvX - .1);

    float Pball = fract(pow(ball,6.));
    float blackness = (dot(Pball,uvX) * 10.) + pow(uvX * 3.5,1.5);
    blackness = clamp(1.-blackness,0.0,1.0);
    uvX = pow(fract(uvX + iTime * 0.75),1.);
    //uvX += ball;


    float mBall = round(Pball);
    float uvY = atan(u.x,u.y)/M_PI;
    float minusuvY = -atan(u.x,u.y)/M_PI;

    float rotY = fract(max(uvY + iTime * 0.5 + Pball, minusuvY));

    float ballY = fract((u.y * mBall + iTime * 0.5));
    float ballX = fract((u.x * mBall * 0.5));
    vec2 ballUV = vec2(ballX,ballY);

    vec2 t_uv = vec2(rotY,uvX);

    t_uv = mix(t_uv,ballUV,mBall);

    vec4 col = texture(iChannel0,t_uv);
    vec4 col2 = texture(iChannel1,t_uv);

    vec3 color1 = vec3(0.451,0.412,0.988);
    vec3 color2 = vec3(0.000,0.000,0.000);
    vec3 color3 = vec3(0.780,0.749,0.875);

    col.rgb = mix(color1,color2,pow(col.r,0.5));
    col.rgb = mix(col.rgb,color2,blackness);

    col2.r = smoothstep(0.8,0.5,col2.r);
    col2.rgb = mix(color3,vec3(0.,0.,0.),pow(col2.r,0.5));
    col2.rgb = clamp(col2.rgb - blackness,0.0,1.0);

    col.rgb += col2.rgb * 0.5;
    fragColor = vec4(ballX,ballX,ballX,1.0);
    //fragColor = vec4(rotY,uvX,0.,1.0);
    //fragColor = vec4(uvX,uvX,uvX,1.0);
    fragColor = col;
}
