#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float ramka(vec2 uv, vec3 center,float i){
float size=0.08;
float dm = size-0.0082;
uv=uv*clamp(mix(0.,2.0,center.z),-2.,2.4);
uv = rotate2d((120.*i+iTime/180.*PI)*sin(iTime*0.012)) * uv;
float k = max(uv.y-center.y,uv.x-center.x);
float squ = step(k-size,0.0)-step(k-dm,0.);
//float squ = step(max(abs(uv.y-center.y),abs(uv.x-center.x))-0.03,0.0)-step(max(abs(uv.y-center.y),abs(uv.x-center.x))-0.026,0.);
return squ;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy-vec2(0.5);
    uv.x = uv.x*(iResolution.x/iResolution.y);
    vec3 col;
    float squ;
    float t =(iTime)*0.15;

    for (float i=0.;i<1.;i+=0.02){
    //uv = uv*rotate2d(sin(iTime*i)*fract(t/2.));
    squ = ramka(uv,vec3(0.,0.,fract(i*4.-t)),i);
    //squ = ramka(uv,vec3(0.,0.,fract(i*3.-t)),i);
    col = col-vec3(squ)*vec3(255., 182., 193.)/255.;

    }
     //216., 191., 216. thistle
     //255., 245., 238. seashell
     //255., 192., 203. pink
     //255., 182., 193.
    //if (col == vec3(0.)){col = vec3(255./255., 245./255., 238./255.);} 
    if (col.x == vec3(0.).x){col = vec3(255., 192., 203.)/255.;}
    fragColor = vec4(col,1.);
}
