Main {
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    Q = A(U);
    float c = atan(10.*(n.x-s.x-e.y+w.y));
    Q = atan(.5*Q.w)*(sin(-1.+4.*c+(Q.z-1.)*Q.w+vec4(1,2,3,4)));
    //Q = mix(Q,vec4(1,.5,0,1),smoothstep(2.,0.,length(U-.5*R)-42.));

 }

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)

#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)

#define vel(v) .5*(v)*inversesqrt(1.+dot(v,v))

#define inside(v) ((v).x>=0.&&(v).y>=0.&&(v).x<R.x&&(v).y<R.y&&length(v-.5*R)>=39.)

float ln (vec3 p, vec3 a, vec3 b) { 
    return length(p-a-(b-a)*(dot(p-a,b-a)/dot(b-a,b-a)));
}

Main {

    Q = A(U);
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (x*x+y*y<=4)
    if (x!=0||y!=0) {
        vec2 v = vec2(x,y);
        vec4 q = A(U+v);
        if (inside(U+v))
        Q.xy -= .004*(q.w+q.w*q.z)*v/dot(v,v);
        Q.z -= .004*q.w*dot(v/dot(v,v),q.xy);
    }
    Q.y -= .01/R.y;
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy *= 0.;
    if (length(U-.5*R)<41.) Q.xy *= 0.;
    if (iFrame < 1) {
       Q.z = 1.;
       Q.w = 4.;
       if (U.x<.5*R.x&&U.y<.5*R.y) Q.w = .5;
       else if (U.x<.5*R.x) Q.w = 2.;
       else if (U.y<.5*R.y) Q.w = 1.;

    }
    if (iMouse.z>0.&&length(U-iMouse.xy)< 8.) Q.x = -1.;
}

Main {

    Q = A(U);
    vec4 dQ = vec4(0);
    #define K (1./8.)
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (x!=0||y!=0) {
        vec2 v = vec2(x,y);
        vec2 W = U+v;
        vec4 q = A(W);

        vec2 a = vel(Q.xy),
             b = vel(q.xy)+v;
        float ab = dot(v,b-a);
        if (inside(W)&&abs(ab)>0.) {
            float i = dot(v,(0.5*v-a))/ab;
            float j = .50;
            float k = .50;
            float wa = K*Q.w*min(i,j)/j;
            float wb = K*q.w*max(k+i-1.,0.)/k;
            dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
        } else {
            dQ += K*Q.w*vec4(Q.xyz,1);
        }

    }
    if (dQ.w>0.) dQ.xyz /= dQ.w;
    Q = dQ;
}

