// Fork of "wondering light" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-09-19 03:32:07
#define R iResolution.xy
void mainImage(out vec4 O,vec2 F)
{
    F=(F-R*.5)/min(R.x,R.y)/.5;
    O-=O;
    O+=.5+.5*dot(vec3(F,sqrt(-F.x*F.x+1.-F.y*F.y)),normalize(vec3(.5)));
}
