void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    uv.x = uv.x * iResolution.x/iResolution.y;
    float colDR = step (pow(uv.x - 0.018 - 0.05,2.0) / 2.0 + pow(uv.y ,2.0) / 2.5 , 0.001) * step (-pow (uv.x - 0.018 - 0.05,2.0) / 2.0 - pow(uv.y ,2.0) / 2.5 , -0.0004) * (float (uv.x >= 0.018 + 0.05)) + float(abs(uv.y) <= 0.05) * float(abs(uv.x -0.018 + 0.009 - 0.05) <= 0.009);
    //The pattern of the letter D on the right,0.018 is added to each x, so that the left side of the letter D aligns with the Y axis
    float colDL = step (pow(uv.x - 0.018 + 0.11 , 2.0) / 2.0 + pow(uv.y,2.0) / 2.5 , 0.001) * step (-pow(uv.x - 0.018 + 0.11,2.0)/ 2.0 - pow(-uv.y ,2.0) / 2.5 , -0.0004) * (float (uv.x >= 0.018 - 0.11)) + float(abs(uv.y) <= 0.05) * float(abs(uv.x -0.018 + 0.009 + 0.11) <= 0.009);
    //The pattern of the letter D on the left
    float colV = step (-abs(uv.x) * 2.5 + uv.y, -0.005) * step (abs(uv.x) * 2.5 - uv.y , 0.05) * step(uv.y,0.05);
    fragColor = vec4(vec3(colDR + colDL + colV) * col,1.0);
}
