void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.4625+0.5*sin(iTime+0.05*tan(iTime-0.2));
    uv.x=uv.x*iResolution.x/iResolution.y;

    float col = smoothstep(0.1,uv.x+uv.y,2.*sin(iTime));

    // Output to screen
    fragColor = vec4(col+smoothstep(0.1,10000.,0.5*sin(tan(iTime-2.)*iTime)*tan(iTime+2.)),col+0.5*tan(iTime)*cos(iTime-3.14),col-sin(iTime)*sin(iTime)+0.2*sin(2.*iTime+1.),1.0);
}
