void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Floating rectangle position with wave effect
    float floatPos = mod(iTime * 0.5, 1.0);
    float wave = 0.05 * sin(iTime * 4.0); // Wave effect
    float rectWidth = 0.2;
    float rectHeight = 0.1;

    // Calculate rectangle position
    float rectY = 0.5 + wave;

    // Rectangle presence
    float inRect = step(floatPos - rectWidth / 2.0, uv.x) * step(uv.x, floatPos + rectWidth / 2.0) *
                   step(rectY - rectHeight / 2.0, uv.y) * step(uv.y, rectY + rectHeight / 2.0);

    // Colors
    vec3 color = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 0.0, 0.0), floatPos); // Blue to Red
    vec3 backgroundColor = vec3(0.0); // Black

    // Final color
    fragColor = vec4(mix(backgroundColor, color, inRect), 1.0);
}
