float length = 5.0;
float radius = 10.0;
float count = 20.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o;
    o.x = iResolution.x / 2.0;
    vec4 color = vec4(0.0);
    for (float i = 0.0; i < count; i ++) {

       o.x = o.x/(i+1.0) + 20.0*(count-i);
       for (float t =  -0.5; t <= 0.0; t += 0.01) {
    	o.y = (sin(iTime*(count-i)*0.05 + t) + 1.0) /2.0 * iResolution.y;

    	if(distance(fragCoord, o) <= radius)
            color = vec4(vec3(0.0,0.0,1.0) + length * t, 1.0);
        }
    }
    fragColor = color;
}
