void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

vec2 fc(vec2 pos, vec2 c) {

    return vec2(pow(pos.x,2.)-pow(pos.y,2.) + c.x,2.*pos.x*pos.y + c.y);;
}

vec2 iter(vec2 c){

    vec2 pos = vec2(0.);

    for(float t=0.; t<=100.; t+=1.){

        pos=fc(pos,c);
        if(length(pos)>2.){
            return vec2(0.,t);
        }
    }
    return vec2(1.,100.);

}


void mainImage( out vec4 fragColor, in vec2 uv )
{

    float a = floor(float(iFrame));
    float b = -1.37409;
    float c=-0.01234831;
    if(mod(a,4.)<=0.){
    float scale=2.5*pow(2.,-float(iFrame)/200.);
    uv.x = scale*((uv.x-0.5*iResolution.x)/iResolution.y)+b;
    uv.y = scale*(uv.y-0.5*iResolution.y)/iResolution.y+c;

    vec2 cur = vec2(0.,0.);
    vec2 cur2=vec2(uv.x,uv.y);

    vec3 col = vec3(0.); 
    float v=0.;
    float a = scale*850./iResolution.x;
    vec2 holder=vec2(0.);

    for(float j=-1.1*a+b; j<=1.1*a+b; j+=a/50.){
    for(float k=0.6*-a+c; k<=0.6*a+c; k+=a/50.){

     cur=vec2(j,k);

     if(length(cur-cur2)<0.55*scale/3.){
     vec2 holder2=iter(cur);
     if(holder2.x!=1.){

     holder=vec2(j,k);
     v=max(v,pow(2.,-25.*length((uv-holder)/scale)));   
     }
     }

    }
    }
    col=vec3(v);
    fragColor = vec4(col,1.0);
    }else{
    uv = uv / iResolution.xy;
    fragColor = texture(iChannel0, uv);     
    }
}
