void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord),0);

}

vec4 rose(vec2 pos,vec2 pixelPos,float radius, float n, float d){
    float k = n/d;
    float speed = 0.5;
    float x = radius * cos(k * iTime * speed) * cos(iTime * speed);
    float y = radius * cos(k * iTime * speed) * sin(iTime * speed);
    vec2 shape = pos + vec2(x,y);
    return (length(shape - pixelPos) - 0.4) * vec4(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 clr = texelFetch(iChannel0, ivec2(fragCoord),0);
    vec4 clr1 = rose(iResolution.xy / 2.0,fragCoord, 100.,4.0 , 7.0);

    fragColor = min(clr,clr1);
    if(iFrame == 0) fragColor = vec4(1.0);

}
