void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 id_col = floor((uv*10.0)+10.0);
    #define SZ 0.07
    vec2 gv = fract(fragCoord*SZ)-.5;

#define CALIVE vec3(.1)+vec3(id_col.x*0.0525, .1, id_col.y*0.0425)
#define CDEAD vec3(.0)

    float value = texture(iChannel0,uv*SZ).r;
    vec3 col = mix(CDEAD,CALIVE,value*value);
    col*=smoothstep(0.1,0.0,length(gv)-0.35);

    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
    if (1.0 > iTime)
    {
        float r0 = texture(iChannel2, uv).r;
        fragColor.r = r0 < 0.4 ? 1.0 : 0.0;
    }
    else
    if (0 != iFrame % 30)
    {
        fragColor.r = texture(iChannel0, uv).r;
    }
    else
    {
        fragColor.r = getPixelState(uv, iChannel1, iResolution.xy);
    }

    if (iMouse.z>0.5 && distance(iMouse.xy,uv)<0.01)
        fragColor.r = 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor.r = texture(iChannel0, uv).r;
}

float getPixelState(in vec2 uv, in sampler2D buffer, in vec2 iResolution)
{
    float result = 0.0;

    int nr = 0;
    for (float i = -1.0; i <= 1.0; i += 1.0)
    for (float j = -1.0; j <= 1.0; j += 1.0)
    {
        float value = texture(buffer, uv+(vec2(i,j)/iResolution.xy)).r;
        nr += value > 0.0 ? 1 : 0;
    }

    bool alive = texture(buffer, uv).r > 0.0;

    if (alive)
    {
        if (2 > nr || 3 < nr)
            result = 0.0;
        if (3 <= nr && 4 >= nr || 8 == nr)
            result = 1.0;
    }
    else
    {
          if (3 == nr)
            result = 1.0;
    }

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 previous = texture(iChannel1, uv)*.98;
    vec4 current  = texture(iChannel0, uv);

    fragColor = max(previous, current);
}
