//Buffer A is where it is at
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;

    //distance the ray travelled was saved in alpha channel
    float maxDist = texture(iChannel0,uv).a;

    vec2 texel = 1./iResolution.xy;
    vec4 total_color;
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }

    fragColor = 1e2*(1.+max(-.1,sin(iTime)))*total_color*exp(-maxDist*maxDist);


}

vec3 ifs_color, ro, tc;
float ifs_scale = 1.07, maxDist, hit=1.;

mat2 rot(float a) {return mat2(cos(a),-sin(a),sin(a),cos(a));}

float de(vec3 z) {


    z.xy *= rot(iTime/4.);
    z.yz *= rot(6.17/10.);
    z.xz *=  rot(5.5/7.);   
    vec3 min_vtx;
    vec3 orig_z = z;
    int n=0;
    float min_dist,dist_to_vtx;
    ifs_color = vec3(0.);
    for (int i=0; i<17; i++) { 
        min_vtx = d12[0];
        min_dist=length(z-d12[0]);
        for (int j=1; j<24; j++) {    
            dist_to_vtx=length(z-d12[j]); 
            if (dist_to_vtx<min_dist) {min_vtx=d12[j]; min_dist=dist_to_vtx;}   
        }
        z = min_vtx + ifs_scale*(z-min_vtx);    
        n++;
    }

    float lz = length(z);
    float dz = pow(ifs_scale, float(n) );
    ifs_color = cos(z*3.) ;
    return lz / dz; 

}

float march(vec3 ro, vec3 rd) {
    float t = 0.0;
    float eps = 5e-4, distfac=10.,d;
    float hitThreshold = eps;
    for(float i = 0.; i < 6.; i++) {
        d = de(ro + rd*t); 
        t += .48*d;
        hitThreshold = eps*(1.+distfac);  
        if  (t > .2+.1*sin(iTime/2.)) tc += ifs_color*ifs_color*exp(-i*i/1e1);
    }  
    return t;
}

vec3 render(vec3 ro, vec3 rd) {
    float t = march(ro, rd);
    maxDist = t;   
    return tc*hit;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y*.8;
    ro = vec3(0,0,-1.55); 
    vec3 rd = normalize( vec3(p,1.4) );



    vec3 col = render(ro, rd);
    col = 1. - exp(-col);
    fragColor = vec4( pow(col,vec3(16)), maxDist);

}
