//rotateUV function https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation, vec2 mid)
{
    float cosAngle = cos(rotation);
    float sinAngle = sin(rotation);
    return vec2(
        cosAngle * (uv.x - mid.x) + sinAngle * (uv.y - mid.y) + mid.x,
        cosAngle * (uv.y - mid.y) - sinAngle * (uv.x - mid.x) + mid.y
    );
}

//I simplfied the tile map set up from https://www.shadertoy.com/view/wtdSzX So that it is even simpler
float hexGradient(vec2 uv)//Draw hexagon gradient
{    
    // Helper vector. If you're doing anything that involves regular triangles or hexagons, the
    // 30-60-90 triangle will be involved in some way, which has sides of 1, sqrt(3) and 2.
    vec2 s = vec2(1.7320508, 1); // scale factors for hexagon
    uv = abs(uv);
    return max(dot(uv, s*.5), uv.y); // Hexagon gradient
}
float drawHex(vec2 uv, float scale, float width)
{
    //Draw hexagon gradient
    vec2 s = vec2(1.7320508, 1); // scale factors for hexagon
    uv = abs(uv);
    float hexGradient = max(dot(uv, s*.5), uv.y); // Hexagon gradient

    //Mask gradient into sharp hex shape
    width *= 0.1f;
    float scale2 = 1.0/scale;
    float hexa = 1.0-floor(hexGradient*(scale2));//Inner mask
    float hexb =     floor(hexGradient*(scale2* (1.0+(width*scale2))));//outer mask
    return hexa*hexb;

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;// Get UV's
    uv -= 0.5f;//center UV's
    uv.x *= iResolution.x/iResolution.y;//Scale UV's

    uv = rotateUV(uv,iTime,vec2(0.0,0.0));//Comment this line out to stop rotation
    float hex = drawHex(uv,0.3,0.3);//This line draws the hexagon
    //float hex = hexGradient(uv);//This line draws a gradient version

    vec3 col = vec3(uv.xy,hex);


    fragColor = vec4(col,1.0);
}
