void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv /= 10.0;
    uv = sin(uv + intBitsToFloat(floatBitsToInt(uv.x) | (floatBitsToInt(uv.y + iTime) << 5)));
    uv = sin(uv + intBitsToFloat(floatBitsToInt(uv.x) | (floatBitsToInt(uv.y + exp(iTime)) << 3)));

    // Time varying pixel color
    vec3 col = vec3(fract(uv.yyx + vec3(length(sin(uv)),0.0,0.0)) * min(pow(max(1.04-length(uv), 0.06), 1.4), 1.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
