//////////////////////////////////////////////////////
// “Expanding Bubble Tiles”
//////////////////////////////////////////////////////

#define STEPS 80
#define FAR_CLIP 50.0
#define Epsilon 0.002

// Simple pastel palette
vec3 pastelPalette(float t) {
    // fade from aqua to soft orange
    return mix(vec3(0.5,0.9,0.8), vec3(1.0,0.75,0.5), t);
}

// Sphere SDF
float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

// Repeat function
vec3 repeat(vec3 p, vec3 c) {
    return mod(p, c) - 0.5*c;
}

// Map: repeated spheres
float mapFun(vec3 pos, float time) {
    // tile spacing
    vec3 cell = repeat(pos, vec3(2.0, 2.0, 2.0));

    // radius changes with time
    float r = 0.5 + 0.2*sin(time + floor(pos.x+pos.y+pos.z));

    return sdSphere(cell, r);
}

vec3 raymarch(vec3 ro, vec3 rd, float time){
    float t=0.0;
    vec3 col = vec3(0.0);
    for(int i=0; i<STEPS; i++){
        if(t>FAR_CLIP) break;
        vec3 p = ro + rd*t;
        float d = mapFun(p, time);
        if(d<Epsilon) {
            // color by distance from origin
            float c = clamp(length(p)*0.07,0.0,1.0);
            vec3 base = pastelPalette(c);

            // optional “ink outline” effect
            // stronger outline if d was very close
            float outline = smoothstep(0.005, 0.0, abs(d));
            col = mix(base, vec3(0.0), outline*0.4);
            break;
        }
        t+=d*0.5;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/ iResolution.y;

    vec3 ro = vec3(0.0,0.0,-5.0);
    vec3 rd = normalize(vec3(uv, 1.6));

    float time = iTime;
    // rotate camera slightly in x-y
    float angle = 0.2*sin(time*0.3);
    mat2 rm = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    rd.xy = rm * rd.xy;

    // get color
    vec3 col = raymarch(ro, rd, time);

    fragColor = vec4(col,1.0);
}
