void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalizza le coordinate
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    // Parametri animazione
    float time = iTime * 0.5;
    vec2 center = vec2(
        cos(time) * 0.3,
        sin(time * 0.7) * 0.2
    );

    // Crea il bulbo di luce
    float radius = length(uv - center);
    float glow = exp(-radius * 3.0);

    // Aggiungi pulsazione
    glow *= 0.8 + 0.3 * sin(time * 2.0);

    // Colore base del bulbo
    vec3 color = vec3(1.0, 0.8, 0.6);

    // Aggiungi variazione di colore
    color = mix(
        color,
        vec3(0.6, 0.8, 1.0),
        sin(radius * 8.0 - time) * 0.2 + 0.2
    );

    // Applica il glow al colore
    vec3 finalColor = color * glow;

    // Aggiungi un po' di ambient glow
    finalColor += color * 0.1 * pow(glow, 3.0);

    // Soft fade ai bordi
    finalColor *= smoothstep(1.0, 0.2, length(uv));

    fragColor = vec4(finalColor, 1.0);
}
