vec2 intersectAABB(vec3 rayOrigin, vec3 rayDir, vec3 boxMin, vec3 boxMax) {
    vec3 tMin = (boxMin - rayOrigin) / rayDir;
    vec3 tMax = (boxMax - rayOrigin) / rayDir;
    vec3 t1 = min(tMin, tMax);
    vec3 t2 = max(tMin, tMax);
    float tNear = max(max(t1.x, t1.y), t1.z);
    float tFar = min(min(t2.x, t2.y), t2.z);
    return vec2(tNear, tFar);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    vec3 aabb1 = vec3(cos(iTime), sin(iTime), -cos(iTime)) * 10.0;
    vec3 aabb2 = aabb1.yzx;
    aabb1 = cross(aabb1, aabb2);
    aabb2 = cross(aabb2, aabb1);
    vec2 inter = intersectAABB(vec3(0.0, 0.0, -2000.0), normalize(vec3(uv, 1.0)), aabb1, aabb2);
    if(inter.y > inter.x)
    {
        fragColor = vec4(0.0);
        return;
    };
    if((inter.x < 0.0) && (inter.y < 0.0))
    {
        fragColor = vec4(0.0);
        return;
    };

    fragColor = vec4(abs(inter.y - inter.x)) / 1000.0;
}
