#define AA 2.0

const uint BODY = 1u;
const uint PLATE = 2u;
const uint ICING = 3u;
const uint TOPPING = 4u;
const uint STRAWBERRY = 5u;

void mouth(in vec3 p, inout Hit res, uint type) {
    switch (type) {
    case 0u: {
        vec3 pm = p + vec3(0., 0.38, 0.38);
        // float tooth = sd_cone(pm * vec3(1., -1., 1.) + vec3(0.2, 0.15, 0.0) +
        //                           vec3(-0.13, 0.0, 0.09),
        //                       vec2(0.2, 0.1), 0.05);

        vec4 umuer = sdBezier(pm + vec3(-0.02, -0.18, 0.089999974),
                              vec3(0.01, -0.12, 0.55), vec3(0.06, 0.19, 0.),
                              vec3(0.01, 0.01, -0.04));
        umuer.x -= 0.06 + (1. - umuer.y) * 0.04;
        umuer += noise(umuer.yzx * 100.) * 0.01 * pow(umuer.y, 3.);

        float noiseier = noise(umuer.yzx * 900.) * 0.0003;

        float exterrior =
            sd_ellipsoid(pm, vec3(0.19000003, 0.24000011, 0.24000005));
        exterrior = length(pm - vec3(0., -0.03, 0.)) - 0.24;
        exterrior = smax(exterrior, -(p.y + 0.424), 0.01);

        vec4 tongue = sdBezier(pm * vec3(.7, 1., 1.) + vec3(0.0, 0.0, -0.03),
                               vec3(0.0, -0.1, 0.1), vec3(0.01, 0.20, -0.00),
                               vec3(0.03, 0.04, -0.18));
        tongue.x += -0.18 * (1. - tongue.y) - 0.033 * pow(tongue.y, 2.);
        float bending_plane =
            max((pm.y + 0.0438), (length(pm + vec3(0., 0., -0.2)) - .373));
        tongue.x = max(tongue.x, -(p.y + 0.45));
        // tongue.x = smin(tongue.x, bending_plane, 0.057);
        // tongue += noiseier; // * pow(umuer.y, 3);

        exterrior = smax(exterrior, -umuer.x, 0.08);
        // tongue = max(tongue, exterrior);

        res.t = max(res.t, -exterrior);
        // res = smin(res, tongue.x, 0.03);
        res = hit_min(res, Hit(tongue.x, pm, 7u));
        // res = min(res, tooth); // fake
        break;
    };
    case 1u: {
        float wiggl = cos(iTime * 11. + PI) * 0.01;
        vec3 pb = vec3(abs(p.x), p.yz);
        pb += vec3(0., 0.23 + wiggl, 0.5);
        pb = erot(pb, vec3(0.0, 0.0, 1.0), -PI / 4.);
        pb = abs(pb) - vec3(0.1, 0.01, 0.0001);
        float box = length(max(pb, vec3(0.)));
        res = hit_min(res, Hit(box, pb, 6u));
        break;
    };
    }
}

float topping(vec3 p) {
    float ring = sd_torus(p + vec3(0., 0.34, 0.0), vec2(0.38, 0.1));

    vec3 q = erot(p, normalize(vec3(-0.2, 1., 0.1)), 3.);
    float arg = q.x * q.z * 100. + iTime;
    float bumps = cos(q.y * 100.);
    bumps = bumps * 0.5 + 0.5;
    bumps *= 0.01;
    ring += bumps;

    p = erot(p, vec3(0., 1., 0.), p.y * 3.);
    float rect = sd_rect(p.xz, vec2(0.5));

    p = erot(p, vec3(0., 1., 0.), PI / 4.);
    float neigh = sd_rect(p.xz, vec2(0.4));

    float res = opSU(rect, neigh, 0.1);
    res += p.y + 0.2;
    res = -opSU(-res * 0.5, -sd_sphere(p, 0.5), 0.1);
    res = smin(ring, res, 0.01);

    return res;
}

float sd_strawberry(vec3 p) {
    float d = 1e9;
    d = length(p) - 1.;
    d = sd_egg(p, 1., 0.2, 0.1);
    d = blend(d, -(p.z - -0.37), 15.1);
    d = blend(d, -sd_sphere(p - vec3(0., -0.11, -0.3), 0.5), 3.);

    return d;
}

vec3 RO = vec3(0.), RD = vec3(0.);
Hit map(vec3 p) {
    float t = iTime;
    float wiggl = cos(t * 11. + PI) * 0.01;
    float wiggle = cos((p.y + t) * 11.) * 0.01;
    float wigglee = cos((p.y + t + 0.05) * 11.) * 0.02;

    Hit res = default_hit();

    // body base
    float bod;
    {
        vec3 pa = vec3(0.0, -0.4, 0.0);
        float body =
            sd_capped_cone(p, pa, vec3(0., -0.01 + wigglee, 0.), 0.4, 0.3).z -
            0.1;
        body = body + wiggle * smoothstep(-0., -0.5, p.y) *
                          smoothstep(0.2, .5, abs(p.x));
        res = hit_min(res, Hit(body, p - pa, BODY));
        bod = body;
    }

    vec2 bbox_bottom = ibox(RO - vec3(0., -0.4, 0.), RD, vec3(.8, 0.4, 0.8));
    if (bbox_bottom.x > 0. || bbox_bottom.y > 0.) {

        // plate
        {
            vec3 pa = p - vec3(0., -0.14, 0.);
            vec2 q = vec2(length(pa.xz) - .2, -pa.y);
            float plate = sd_joint2d(q, .8, 1.1, 0.02);
            res = hit_min(res, Hit(plate, pa, PLATE));
        }

        // { mouth(p, res, uint(step(0.5, mod(t * 0.6, 1.)))); }
        // { mouth(p, res, uint(step(0.5, 0))); }
        // { mouth(p, res, 1); }
    }

    vec2 bbox_up = ibox(RO - vec3(0., 0.2, 0.), RD, vec3(.5, 0.4, 0.7));
    if (bbox_up.x > 0. || bbox_up.y > 0.) {
        float top = 1e9;
        vec3 ptop;
        {
            vec3 pi = p + vec3(0., 0.12 + wiggl, 0.0);
            float d = length(pi.xz);
            float ang = atan(pi.z, pi.x) + PI / 2.;
            float off = PI / 4. + 0.0, range = 0.1;
            float amplifier = 1. - (smoothstep(-range, 0.5, ang + off) -
                                    smoothstep(-0.4, range, ang - off));
            vec3 dripping = sd_capped_cone(pi, vec3(0., 0., 0.0),
                                           vec3(0.0, 0.14, 0.0), 0.4, 0.303) +
                            0.011 * (1. - amplifier);
            dripping.z += -0.113;
            float waveFactor = (d > 1. ? 0. : .5);
            float wave = sin(ang * 20. * waveFactor) * .067 +
                         sin(ang * 40. * waveFactor + .7) * 0.038 +
                         sin(ang * 25. * waveFactor + 0.6) * 0.027;
            wave *= amplifier * 0.6 * smoothstep(-0.35, 0.6, length(p.xz));

            vec3 icingWave = vec3(0, 0, pi.y - (wave + .03));
            vec3 icing = carve(dripping, icingWave, .2);

            top = min(top, icing.z);
            ptop = pi;
        }

        {
            vec3 po = p - vec3(0., 0.2 - wiggle, 0.);
            vec4 ahoge =
                sdBezier(po, vec3(0., -0.09, 0.), vec3(-0.03, 0.08, -0.01),
                         vec3(-0.08, 0.01 - wigglee * 0.4, 0.));
            ahoge.x -= 0.027 * ahoge.y + (1. - ahoge.y) * 0.008;
            top = smin(top, ahoge.x + 0.003, 0.035);
        }
        res = hit_min(res, Hit(top, ptop, ICING));
        {
            vec3 po = p;
            po.y += wiggl;
            vec3 pt = vec3(abs(po.x), po.yz) + vec3(-0.20, -0.20, 0.14);
            float scale = .22;
            float topping = topping(pt / scale) * scale;
            // res = smin(res, topping, 0.03);
            res = hit_min(res, Hit(topping, pt, TOPPING));

            pt -= vec3(0., -0.02, 0.01);
            pt.yz *= rot(0.2);
            pt.yx *= rot(-0.3);
            float s = 0.09;
            float strawberry = sd_strawberry(pt / s) * s;
            res = hit_min(res, Hit(strawberry, pt, STRAWBERRY));
        }
    }

    return res;
}

Hit trace(in vec3 ro, in vec3 rd) {
    vec2 bbox = ibox(ro, rd, vec3(.80, .8, 0.8));
    if (bbox.x < 0. && bbox.y < 0.) {
        return default_hit();
    }
    RO = ro, RD = rd;
    float t = bbox.x;
    for (int i = min(0, iFrame); i < 100; ++i) {
        vec3 p = ro + rd * t;
        Hit d = map(p);
        if (abs(d.t) < 0.0001) {
            return Hit(t, d.pos, d.id);
        }
        t += d.t;
        if (t > bbox.y) {
            break;
        }
    }
    return default_hit();
}

vec3 get_norm(vec3 p) {
    mat3 k = mat3(p, p, p) - mat3(0.0001);
    return normalize(map(p).t - vec3(map(k[0]).t, map(k[1]).t, map(k[2]).t));
}

mat3 get_cam(vec3 eye, vec3 target) {
    vec3 zaxis = normalize(target - eye);
    vec3 xaxis = normalize(cross(vec3(0., 1., 0.), zaxis));
    vec3 yaxis = cross(zaxis, xaxis);
    return mat3(xaxis, yaxis, zaxis);
}

float ambientOcclusion(vec3 p, vec3 n) {
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for (int i = min(1, iFrame + 1); i <= steps; i++) {
        float d = (float(i) / float(steps)) * delta;
        a += weight * (d - map(p + n * d).t);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

// https://iquilezles.org/articles/rmshadows/
float softshadow(in vec3 ro, in vec3 rd, float mint, float maxt, float w) {
    float res = 1.0;
    float t = mint;
    for (int i = min(0, iFrame); i < 25 && t < maxt; i++) {
        float h = map(ro + t * rd).t;
        res = min(res, h / (w * t));
        t += clamp(h, 0.005, 0.50);
        if (res < -1.0 || t > maxt)
            break;
    }
    res = max(res, -1.0);
    return 0.25 * (1.0 + res) * (1.0 + res) * (2.0 - res);
}

// https://www.shadertoy.com/view/dltGWl
vec3 lighting(int type, vec3 nor, vec3 ldir, vec3 rd, vec3 lcol, vec3 albedo,
              vec3 sscolor, vec3 ssradius, float roughness, float ior) {
    float ndl = dot(nor, ldir);
    float pndl = clamp(ndl, 0., 1.);
    float nndl = clamp(-ndl, 0., 1.);

    // subsurface scattering
    vec3 sss = .2 * exp(-3. * (nndl + pndl) / (ssradius + .001));

    vec3 h = normalize(ldir - rd); // half vector
    float ndh = dot(nor, h);       // Nâ‹…H

    // ggx / Trowbridge and Reitz specular model approximation
    float g = ndh * ndh * (roughness * roughness - 1.) + 1.;
    float ggx = roughness * roughness / (PI * g * g);

    float fre = 1. + dot(rd, nor);
    float f0 = (ior - 1.) / (ior + 1.);
    f0 = f0 * f0;
    float refl = f0 + (1. - f0) * (1. - roughness) * (1. - roughness) *
                          pow(fre, 5.); // reflectivity

    pndl = mix(pndl * 0.5 + 0.5, max(pndl, 0.), 0.35);
    return lcol *
           (pndl * (albedo + refl * ggx) + albedo * sscolor * ssradius * sss);
}

void render( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord/iResolution.xy - .5)
            * vec2(iResolution.x / iResolution.y, 1.);

    vec3 ro = vec3(0., .4, -3);
    vec3 lo = ro + vec3(0, 0, 1);

    vec2 m = vec2(0., 0.);
    if (iMouse.z > 0.) {
        m = (iMouse.xy/iResolution.xy - .5)
            * vec2(iResolution.x / iResolution.y, 1.);

        m.x += m.x * PI * 0.5;
        m.y += m.y * PI * 0.7;
    }
    m.x = clamp(m.x, -PI / 3., PI / 3.);
    m.y = clamp(m.y, -PI / 4., 0.2);
    ro.xz *= rot(-m.x);
    ro.zy *= rot(m.y);

    float focal_dist = 1.6;
    mat3 cmat = get_cam(ro, vec3(0., -0.15, 0.));
    vec3 rd = normalize(cmat * vec3(uv, focal_dist));

    vec3 col = vec3(.13);
    vec4 emit = vec4(vec3(0.), 1e9);
    Hit hit = trace(ro, rd);
    vec3 nplane = vec3(0, 1., 0.);
    float plane = -(dot(ro, nplane) + 0.53) / dot(rd, nplane);
    if (plane < hit.t && plane > 0.) {
        hit = Hit(plane, ro + rd * plane, 0u);
    }
    if (hit.t < 1e9) {
        vec3 pos = ro + rd * hit.t;
        vec3 nor = get_norm(pos);
        float shad = dot(nor, normalize(vec3(1., 1., -1.)));
        vec3 albedo = vec3(1.);

        vec3 lpos = vec3(1.1, -0.12, -1.4);
        lpos = vec3(2., 1., -2.6);
        vec3 ldir = normalize(lpos - pos);

        float rou = 1.3, ior = 1.5;
        if (hit.id == BODY) {
            albedo = vec3(1., 0.7, 0.1) * 1.5;
            rou = 0.3;
            ior = 1.3;

            float wiggl = cos(iTime * 11. + PI) * 0.01;

            vec2 p = hit.pos.xy;
            vec2 pp =
                vec2(abs(p.x), p.y - 0.2) - vec2(0.13, 0.08) + vec2(0., wiggl);
            vec2 ppr = pp;
            if (p.x > 0.) {
                pp.x *= -1.;
            }

            float t = easeInBack(stepNoise(iTime * .5, 2.));
            float shift = t * -0.022;

            vec2 blp = vec2(-pp.y, pp.x) - vec2(0.07, -0.02) - vec2(shift, 0.);
            blp *= rot(-0.4);
            float blink = sd_joint2d(blp, 0.041, 0.4, 0.018);

            vec3 brown = vec3(0.04, 0.015, 0.01) * 1.2;
            float eye = sd_egg2d(pp + vec2(0., -shift), 0.05, 0.07);
            eye = blend(eye, -(pp.y + 0.1), 29.);
            eye = blend(eye, -blink, 80.);
            col = mix(col, brown, step(eye, 0.));
            emit.rgb = mix(emit.rgb, brown, AAstep(eye));
            emit.w = min(emit.w, eye);

            vec2 bp = ppr - vec2(0.03, 0.02) + vec2(0., -shift);
            bp *= rot(1.1);
            float brows = sd_line(bp, 0.023, 0.019);
            brows = blend(brows, -(bp.x + 0.01), 250.);
            brows = blend(brows, eye, -200.);
            brows = blend(brows, -blink, 100.);
            emit.rgb = mix(emit.rgb, brown, AAstep(brows));
            emit.w = min(emit.w, brows);

            float downlight =
                sd_egg2d(pp - vec2(0., -0.04 + shift), 0.030, 0.02);
            downlight = blend(downlight, eye + 0.01, 330.);
            col = mix(col, brown * 3.0, step(downlight, 0.));
            emit.rgb = mix(emit.rgb, brown * 3.0, AAstep(downlight));

            float highlight = sd_sphere(pp - vec2(-0.02, 0.02), 0.01);
            highlight = blend(highlight, eye + 0.005, 250.);
            emit.rgb = mix(emit.rgb, brown * 10.0, AAstep(highlight));

            vec2 sp = p - vec2(-0.02, 0.15 - wiggl);
            sp *= rot(-0.40);
            float smirk = sd_joint2d(sp.yx, 0.04, -0.40, 0.01);
            sp = vec2(abs(p.x), p.y) - vec2(0.10, 0.151) + vec2(0., wiggl);
            sp *= rot(-2.05);
            float sides = sd_joint2d(sp, .07, 0.41, 0.01);
            smirk = smin(smirk, sides, 0.0002);
            emit.rgb = mix(emit.rgb, brown, AAstep(smirk));
            emit.w = min(emit.w, smirk);

            vec2 pb = vec2(abs(p.x) - 0.13, p.y - 0.02) - vec2(0.11, 0.17) +
                      vec2(0., wiggl);
            pb *= vec2(0.6, 1.);
            float blush = sd_sphere(pb, 0.03);
            albedo = mix(albedo, vec3(0.8, 0.2, 0.2),
                         smoothstep(0.11, -0.15, blush));
        }
        if (hit.id == PLATE) {
            albedo = vec3(1.);
        }
        if (hit.id == ICING) {
            albedo = vec3(0.04, 0.015, 0.01) * 1.3;
            rou = 0.02;
            ior = 0.3;
        }
        if (hit.id == TOPPING) {
            albedo = vec3(1.3);
        }
        if (hit.id == STRAWBERRY) {
            albedo = vec3(0.9, 0.0, 0.0);
        }
        vec3 ssr = vec3(1., 0.9, 0.6);
        col = lighting(1, nor, ldir, rd, vec3(1.), albedo, albedo * 0.75, ssr,
                       rou, ior);

        col *= ambientOcclusion(pos, nor);

        float shadow =
            softshadow(pos + nor * 0.01, normalize(lpos - pos), 0.01, 0.35, 0.40);
        col *= smoothstep(-0.5, 1.0, shadow);
    }
    if (hit.id == 0u) {
        float wiggl = cos(iTime * 11. + PI) * 0.01;
        col = vec3(.4, 0.7, 0.2);
        col *= smoothstep(0.5, 1.2, length(hit.pos.xz));
        col *= smoothstep(
            -0.5, 1.5,
            clamp(length(hit.pos.xz - vec2(-0.5, 0.36) - wiggl) - 0.24, 0.2,
                  1.));
    }

    vec3 background = vec3(1.15, .8, .36) * .9;
    col = mix(col, background, smoothstep(4., 12., hit.t));

    // col = ACESFilm(col);
    col = color2agx(col);
    // col = smoothstep(0., 1., col);

    if (emit.w < 1e9 && hit.pos.z < 0.) {
        col = mix(col, emit.rgb, step(emit.w, 0.));
    }

    // vignette
    vec2 in_uv = fragCoord / iResolution.xy;
    col *= smoothstep(-0.02, .01,
                      in_uv.x * in_uv.y * (1. - in_uv.x) * (1. - in_uv.y));

    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

void mainImage(out vec4 O,vec2 C){
    float px=1./AA,i,j;vec4 cl2,cl;
    if(AA==1.){render(cl,C);O=cl;return;}
    for(i=0.;i<AA +min(iTime,0.0);i++){for(j=0.;j<AA;j++){
    vec2 C2 = vec2(C.x+px*i,C.y+px*j);
    render(cl2,C2);cl+=cl2;
    }}cl/=AA*AA;O=cl;
}

const float PI = acos(-1.);
const float TAU = 2. * PI;

float AAstep(float thre, float val) {
    return smoothstep(-.5, .5, (val - thre) / min(0.03, fwidth(val - thre)));
}
float AAstep(float val) {
    return AAstep(val, 0.);
}

struct Hit {
    float t;
    vec3 pos;
    uint id;
};

Hit default_hit() { return Hit(1e9, vec3(0.), 999u); }

Hit hit_min(Hit a, Hit b) {
    if (a.t < b.t) {
        return a;
    }
    return b;
}
Hit hit_max(Hit a, Hit b) {
    if (a.t > b.t) {
        return a;
    }
    return b;
}

mat2 rot(float x) {
    float c = cos(x), s = sin(x);
    return mat2(c, -s, s, c);
}

vec3 erot(vec3 p, vec3 d, float ro) {
    return mix(dot(p, d) * d, p, cos(ro)) + cross(p, d) * sin(ro);
}

float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.0);
    return min(a, b) - h * h * 0.25 / k;
}

// float smin(float a, float b, float k) { return smax(a, b, -k); }
float smax(float a, float b, float k) {
    k *= 1.4;
    float h = max(k - abs(a - b), 0.0);
    return max(a, b) + h * h * h / (6.0 * k * k);
}

vec3 carve(in vec3 p1, in vec3 p2) {
    return p1.z > -p2.z ? p1 : vec3(p2.xy, -p2.z);
}

vec3 carve(in vec3 p1, in vec3 p2, float k) {
    return vec3(p1.z > -p2.z ? p1.xy : p2.xy, -smin(-p1.z, p2.z, k));
}

float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float hash31(vec3 p3) {
    p3 = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}

// 3d noise function (linear interpolation between hash of integer bounds)
float noise(vec3 uv) {
    vec3 fuv = floor(uv);
    vec4 cell0 = vec4(hash31(fuv + vec3(0, 0, 0)), hash31(fuv + vec3(0, 1, 0)),
                      hash31(fuv + vec3(1, 0, 0)), hash31(fuv + vec3(1, 1, 0)));
    vec2 axis0 = mix(cell0.xz, cell0.yw, fract(uv.y));
    float val0 = mix(axis0.x, axis0.y, fract(uv.x));
    vec4 cell1 = vec4(hash31(fuv + vec3(0, 0, 1)), hash31(fuv + vec3(0, 1, 1)),
                      hash31(fuv + vec3(1, 0, 1)), hash31(fuv + vec3(1, 1, 1)));
    vec2 axis1 = mix(cell1.xz, cell1.yw, fract(uv.y));
    float val1 = mix(axis1.x, axis1.y, fract(uv.x));
    return mix(val0, val1, fract(uv.z));
}

// https://www.shadertoy.com/view/md2GWW
float stepNoise(float x, float n) {
    float i = floor(x);
    float s = 0.1;
    float u = smoothstep(0.5 - s, 0.5 + s, fract(x));
    return mix(floor(hash11(i) * n), floor(hash11(i + 1.) * n),
               u); // from 0. to n - 1.
}

float easeInBack(float x) {
    float c1 = 1.70158;
    float c3 = c1 + 1.;

    return c3 * x * x * x - c1 * x * x;
}

vec2 ibox(in vec3 ro, in vec3 rd, vec3 boxsize) {
    vec3 m = 1.0 / rd;
    vec3 n = m * ro;
    vec3 k = abs(m) * boxsize;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    if (tN > tF || tF < 0.0)
        return vec2(-1.0);
    return vec2(tN, tF);
}

float sd_sphere(vec3 p, float r) { return length(p) - r; }
float sd_sphere(vec2 p, float r) { return length(p) - r; }

float sd_box(vec3 p, vec3 h) {
    p = abs(p) - h;
    return length(max(p, 0.)) + min(0., max(p.x, max(p.y, p.z)));
}

float sd_rect(vec2 p, vec2 h) {
    p = abs(p) - h;
    return length(max(p, 0.)) + min(0., max(p.x, p.y));
}

float sd_cone(vec3 p, vec2 c, float h) {
    float q = length(p.xz);
    return max(dot(c.xy, vec2(q, p.y)), -h - p.y);
}

float sd_torus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

float opSU(float a, float b, float k) {
    float h = clamp(.5 + .5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float sdTriangleIsosceles(in vec2 p, in vec2 q) {
    p.x = abs(p.x);
    vec2 a = p - q * clamp(dot(p, q) / dot(q, q), 0.0, 1.0);
    vec2 b = p - q * vec2(clamp(p.x / q.x, 0.0, 1.0), 1.0);
    float s = -sign(q.y);
    vec2 d = min(vec2(dot(a, a), s * (p.x * q.y - p.y * q.x)),
                 vec2(dot(b, b), s * (p.y - q.y)));
    return -sqrt(d.x) * sign(d.y);
}

vec3 sd_capped_cone(vec3 p, vec3 a, vec3 b, float ra, float rb) {
    float rba = rb - ra;
    float baba = dot(b - a, b - a);
    float papa = dot(p - a, p - a);
    float paba = dot(p - a, b - a) / baba;
    float x = sqrt(papa - paba * paba * baba);
    float cax = max(0.0, x - ((paba < 0.5) ? ra : rb));
    float cay = abs(paba - 0.5) - 0.5;
    float k = rba * rba + baba;
    float f = clamp((rba * (x - ra) + paba * baba) / k, 0.0, 1.0);
    float cbx = x - ra - f * rba;
    float cby = paba - f;
    float s = (cbx < 0.0 && cay < 0.0) ? -1.0 : 1.0;

    float px = atan(p.x, p.z) * paba;
    float py = atan(p.y, p.x) * rba;

    return vec3(px, py,
                s * sqrt(min(cax * cax + cay * cay * baba,
                             cbx * cbx + cby * cby * baba)));
}

float sd_joint2d(in vec2 p, in float l, in float a, float w) {
    // if perfectly straight
    if (abs(a) < 0.001) {
        p.y -= clamp(p.y, 0.0, l);
        return length(p);
    }

    // parameters
    vec2 sc = vec2(sin(a), cos(a));
    float ra = 0.5 * l / a;

    // recenter
    p.x -= ra;

    // reflect
    vec2 q = p - 2.0 * sc * max(0.0, dot(sc, p));

    // distance
    float u = abs(ra) - length(q);
    float d = (q.y < 0.0) ? length(q + vec2(ra, 0.0)) : abs(u);

    return d - w;
}

float sd_ellipsoid(vec3 p, vec3 r) {
    float k0 = length(p / r);
    float k1 = length(p / (r * r));
    return k0 * (k0 - 1.0) / k1;
}

float sd_line(vec2 p, float r, float l) {
    l = max(0., l);
    p.y -= clamp(p.y, -l, l);
    return length(p) - r;
}

float sd_egg2d(in vec2 p, in float ra, in float rb) {
    const float k = sqrt(3.0);

    p.x = abs(p.x);

    float r = ra - rb;

    return ((p.y < 0.0)             ? length(vec2(p.x, p.y)) - r
            : (k * (p.x + r) < p.y) ? length(vec2(p.x, p.y - k * r))
                                    : length(vec2(p.x + r, p.y)) - 2.0 * r) -
           rb;
}

float sd_egg(in vec3 p, in float ra, in float rb, float o) {
    vec2 q = vec2(length(p.xz) - o, p.y);
    return sd_egg2d(q, ra, rb);
}

float blend(float a, float b, float k) {
    return log(exp(a * k) + exp(b * k)) / k;
}

// http://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf
// { dist, t, y (above the plane of the curve, x (away from curve in the plane
// of the curve))
float det(vec2 a, vec2 b) { return a.x * b.y - b.x * a.y; }
vec4 sdBezier(vec3 p, vec3 va, vec3 vb, vec3 vc) {
    vec3 w = normalize(cross(vc - vb, va - vb));
    vec3 u = normalize(vc - vb);
    vec3 v = cross(w, u);

    vec2 m = vec2(dot(va - vb, u), dot(va - vb, v));
    vec2 n = vec2(dot(vc - vb, u), dot(vc - vb, v));
    vec3 q = vec3(dot(p - vb, u), dot(p - vb, v), dot(p - vb, w));

    float mn = det(m, n);
    float mq = det(m, q.xy);
    float nq = det(n, q.xy);

    vec2 g = (nq + mq + mn) * n + (nq + mq - mn) * m;
    float f = (nq - mq + mn) * (nq - mq + mn) + 4.0 * mq * nq;
    vec2 z = 0.5 * f * vec2(-g.y, g.x) / dot(g, g);
    // float t = clamp(0.5+0.5*(det(z,m+n)+mq+nq)/mn, 0.0 ,1.0 );
    float t = clamp(0.5 + 0.5 * (det(z - q.xy, m + n)) / mn, 0.0, 1.0);
    vec2 cp = m * (1.0 - t) * (1.0 - t) + n * t * t - q.xy;

    float d2 = dot(cp, cp);
    return vec4(sqrt(d2 + q.z * q.z), t, q.z, -sign(f) * sqrt(d2));
}

// Agx from https://www.shadertoy.com/view/cd3XWr
#define AGX_LOOK 2

// AgX
// ->

// Mean error^2: 3.6705141e-06
vec3 agxDefaultContrastApprox(vec3 x) {
    vec3 x2 = x * x;
    vec3 x4 = x2 * x2;

    return +15.5 * x4 * x2 - 40.14 * x4 * x + 31.96 * x4 - 6.868 * x2 * x +
           0.4298 * x2 + 0.1191 * x - 0.00232;
}

vec3 agx(vec3 val) {
    const mat3 agx_mat =
        mat3(0.842479062253094, 0.0423282422610123, 0.0423756549057051,
             0.0784335999999992, 0.878468636469772, 0.0784336,
             0.0792237451477643, 0.0791661274605434, 0.879142973793104);

    const float min_ev = -12.47393f;
    const float max_ev = 4.026069f;

    // Input transform
    val = agx_mat * val;

    // Log2 space encoding
    val = clamp(log2(val), min_ev, max_ev);
    val = (val - min_ev) / (max_ev - min_ev);

    // Apply sigmoid function approximation
    val = agxDefaultContrastApprox(val);

    return val;
}

vec3 agxEotf(vec3 val) {
    const mat3 agx_mat_inv =
        mat3(1.19687900512017, -0.0528968517574562, -0.0529716355144438,
             -0.0980208811401368, 1.15190312990417, -0.0980434501171241,
             -0.0990297440797205, -0.0989611768448433, 1.15107367264116);

    // Undo input transform
    val = agx_mat_inv * val;

    // sRGB IEC 61966-2-1 2.2 Exponent Reference EOTF Display
    // val = pow(val, vec3(2.2));

    return val;
}

vec3 agxLook(vec3 val) {
    const vec3 lw = vec3(0.2126, 0.7152, 0.0722);
    float luma = dot(val, lw);

    // Default
    vec3 offset = vec3(0.0);
    vec3 slope = vec3(1.0);
    vec3 power = vec3(1.0);
    float sat = 1.0;

#if AGX_LOOK == 1
    // Golden
    slope = vec3(1.0, 0.9, 0.5);
    power = vec3(0.8);
    sat = 0.8;
#elif AGX_LOOK == 2
    // Punchy
    slope = vec3(1.0);
    power = vec3(1.35, 1.35, 1.35);
    sat = 1.4;
#endif

    // ASC CDL
    val = pow(val * slope + offset, power);
    return luma + sat * (val - luma);
}

// <-

vec4 toLinear(vec4 sRGB) {
    bvec4 cutoff = lessThan(sRGB, vec4(0.04045));
    vec4 higher = pow((sRGB + vec4(0.055)) / vec4(1.055), vec4(2.4));
    vec4 lower = sRGB / vec4(12.92);

    return mix(higher, lower, cutoff);
}

vec3 color2agx(vec3 col) {
    // col = toLinear(vec4(col, 1.0)).rgb;

    col = agx(col);
    col = agxLook(col);
    col = agxEotf(col);

    return col;
}
