float sampleMusicA() {
	return 1.1 * (
		texture( iChannel0, vec2( 0.15, 0.25 ) ).x + 
		texture( iChannel0, vec2( 0.30, 0.25 ) ).x);
}
float music() {
    float x = sampleMusicA();
    return x*x;
}
float hash(float p) {
    return sin(p*13411.69348721);
}
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float base(vec2 uv, float t) {
    vec2 ndc = (uv - 0.5) * 2.0;
    float h = 1.0 - dot(ndc,ndc);
    float q = fract(t);
    float hstart = fract(q + h);
    float gray = hstart;
    return 1.0 - gray;
}

float channel(vec2 uv, float t) {
    float themin = 1.0;
    for (int i = 0; i < 3; i++) {
        vec2 uv2 = fract(uv*(float(i)+1.0));
        float h = base(uv2, t);
        themin = min(themin, h);
    }
    return themin;
}

float ichannel(vec2 uv, float t) {
    bool b = channel(uv, t) * music() > hash12(uv*396.0);
    return b ? 1.0 : 0.0;
}

vec3 image(vec2 uv, float t) {
    float x = ichannel(uv, t);
    return vec3(x,x,x);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(image(uv, iTime*0.111),1.0);
}
