void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    highp vec2 uv = fragCoord/iResolution.xy;
    highp vec3 col = vec3(0.2, 0.21, 0.25) * 0.15 * (0.3 + 0.7 * (1.0 - abs(uv.y - 0.5) * 2.0));

    highp float lt = max(0.0, (0.75 - abs(uv.y - 0.5 + sin(iTime * 1.4) * 0.3)));
    lt *= 1.25;

    highp float lineCount = 70.0;
    highp float dline = mod(uv.y, 1.0 / lineCount) * lineCount;
    dline -= 0.6;
    dline = smoothstep(0.0, 0.001, dline);
    dline += lt * dline;
    highp vec2 pl1 = vec2(sin(iTime * 2.37) * 0.2, 0.5);
    highp vec2 pl2 = vec2(1.0 + cos(iTime * 2.17) * 0.2, 0.5);
    highp float d1 = 1.2 - length(uv * vec2(1.0, 0.7) - pl1);
    highp float d2 = 1.2 - length(uv * vec2(1.0, 0.7) - pl2);
    highp float dl = d1 * d1 * d1 + d2 * d2 * d2;
    highp vec3 dlineColor = (vec3(0.0, 0.0, 0.8) * d1 * d1 * 0.5 + vec3(0.8, 0.0, 0.0) * d2 * d2 * 0.5);
    dlineColor *= (0.3 + 0.7 * (1.0 - abs(uv.y - 0.5) * 2.0)) * 0.8;
    col += dlineColor * dline;


    highp float t0 = 0.18 - abs(0.5 - uv.x) / max((uv.y - 0.4) * 1.6, 0.0) ;
    t0 = smoothstep(0.0, 0.001, t0) * 0.6;

    highp float t1 = 0.1 - abs(0.5 - uv.x) / max((1.0 - (uv.y + 0.7)) * 3.0, 0.0);
    t1 = smoothstep(0.0, 0.001, t1) * 0.6;


    highp vec3 colt = vec3(0.0);
    colt += vec3(t0) * (0.1 * vec3(1.0) + 0.9 * dlineColor) * 0.5;
    colt += vec3(t1) * (0.1 * vec3(1.0) + 0.9 * dlineColor) * 0.5;

    colt += lt * colt * 3.5 + lt * colt * 7.5 * vec3(1.0, 1.0, 0.5);
    col += colt;

    highp float t2 = 0.0025 - abs(abs(uv.x - 0.5)- (uv.y - 0.6) * 0.28);
    t2 = smoothstep(0.0, 0.001, t2);
    highp float t3 = 0.0025 - abs(abs(uv.x - 0.5)- (uv.y - 0.2) * 0.28);
    t3 = smoothstep(0.0, 0.001, t3);
    highp float tls = max(t2, t3);

    highp float ltsl = (0.25 - abs(uv.x - (mod(iTime * 0.1, 1.0)))) / 0.25;
    highp float ltsl2 = (0.25 - abs(uv.x - (mod((iTime + 5.0) * 0.1, 1.0)))) / 0.25;
    ltsl = max(ltsl,ltsl2);
    ltsl = min(ltsl, 1.0);
    ltsl = max(ltsl, 0.0);
    ltsl *= ltsl;
    highp vec3 coltl = vec3(1.0) * tls;
    highp vec3 coltl2 = vec3(max(ltsl * tls, 0.0), 0.0, 0.0);
    col = mix(coltl, col, 1.0 - tls * 0.2);
    col = mix(col, coltl2, (tls * ltsl));


    // Output to screen
    fragColor = vec4(col,1.0);
}
