// random single value for 2 inputs -> the values we recive is in the range of (0 - 1) 
float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p+= dot(p,p+34.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    uv *= 5.0;  // zoom factor
    uv.x += iTime * 0.5; // movement speed 

    vec2 gv = fract(uv)- 0.5;

    vec2 id = floor(uv);        // asign a differnt value for each cell get a randomized value from 0-1 and then 
    float n = Hash21(id);       // split into two groups group 1 stays the same group 2 rotates 
    if (n < 0.5) gv.x *= -1.0;

    // ==================== LINES ====================
    float width_l = 0.15;  // line width
    float d_line = abs(abs(gv.x + gv.y) - 0.5);      // adding two lines with an offset of 0.5 from the center ( looks better ) 
    float line = smoothstep(-.05,.05, d_line - width_l);
    // =============================================


    // ==================== CIRCLES ====================
    float width_c = 0.25;
    float d_circle = length(gv - vec2(-0.5)) - 0.5;
    float circle = smoothstep(.01,-.01, abs(d_circle)- width_c);
    d_circle = length(gv - vec2(0.5)) - 0.5;
    circle += smoothstep(.01,-.01 , abs(d_circle) - width_c); 
    // =================================================

    // switch true/false to choose from lines or circle shapes 
    if(true){
        col+= circle;
    }else{
        col+= line;
    }

    col.bg+= n;  // setting the color with the random value 

    //if(gv.x>.49 || gv.y> .49) col = vec3(1,0,0);  // borders for visibility of the cells 
    fragColor = vec4(col,1.0);
}
