Main {
    float k = 0.;
    vec2 u = 2.*(U-.5*R)/R.y;
    for (float i = 0.; i < 20.; i++) {
        u = vec2(u.x*u.x-u.y*u.y,2.*u.x*u.y)-vec2(.8,.2);
        if (length(u)>4.) break;
        else k += .02*step(length(u),.5);
    }


    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));

    vec4 _n = n.w*(n-Q);
    vec4 _s = s.w*(Q-s);
    vec4 _e = e.w*(e-Q);
    vec4 _w = w.w*(Q-w);


    Q.x += .25*(_n-_s+_e-_w).x;
    Q.y = (n.w*n.y+e.w*e.y+s.w*s.y+w.w*w.y+(.005*(k)))/(n.w+e.w+s.w+w.w);

    if (iFrame < 1) Q = vec4(0);



    Q.w = exp(-15.*k);

    if (U.x<1.||R.x-U.x<1.||R.y-U.y<1.||U.y<1.) Q.y = 0.;


}

