// Author @gfscalco - 2024
// A truchet tile composed of rotated 1/4 circles


#define PI 3.14159265358979323846

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec2 random_vec2(in vec2 _st){
    return fract(sin(_st.xy)*43758.5453123);
}

float circle(in vec2 pixel, in float r)
{
    vec2 pos = vec2(0.5);
    pixel = pixel - pos;
    float angle = atan(pixel.y/pixel.x);
    float radius = length(pixel);
    float mask = smoothstep(r-0.005,r+0.35, radius);
    return (1.0-mask);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;

    //Creating the moving systems
    vec2 moving_st_1 = st;
    moving_st_1.x += 10.0*iTime*0.1;    

    vec2 moving_st_2 = st;
    moving_st_2.x += 7.0*iTime*0.1;

    vec2 moving_st_3 = st;
    moving_st_3.x += 5.0*iTime*0.1;


    //Further layer coordiante system    
    vec2 st_layer1 = moving_st_1;
    st_layer1 *= 25.0;
    vec2 ipos1 =  floor(st_layer1);    
    float dots_1_speed = iTime*(random(ipos1));
    vec2 fpos1 = fract(st_layer1); 
    //Medium layer coordiante system
    vec2 st_layer2 = moving_st_2;
    st_layer2 *= 15.0;
    vec2 ipos2 =  floor(st_layer2);
    vec2 fpos2 = fract(st_layer2);
    //Closest layer coordinate system
    vec2 st_layer3 = moving_st_3;
    st_layer3 *= 5.0;
    vec2 ipos3 =  floor(st_layer3);
    vec2 fpos3 = fract(st_layer3);


    //"stars" in the further layer    
    float dots_1 = step(0.80,circle(vec2(fpos1.x + 1.0*random_vec2(ipos1).y-0.5, fpos1.y + 1.0*random_vec2(ipos1).x-0.5 ),0.02)*(random(ipos1)));

    //"stars" in the medium layer
    float dots_2 = step(0.85,circle(vec2(fpos2.x + 0.8*random_vec2(ipos2).y-0.4, fpos2.y + 0.8*random_vec2(ipos2).x-0.4 ),0.03)*(random(ipos2)));

    //"stars" in the closes layer
    float dots_3 = step(0.90,circle(vec2(fpos3.x + 0.7*random_vec2(ipos3).y-0.35, fpos3.y + 0.7*random_vec2(ipos3).x-0.35),0.04)*(random(ipos3)));


    fragColor = vec4(vec3(dots_1 + dots_2 + dots_3),1.0);
}
