void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2. - iResolution.xy)/iResolution.y;
    float r = uv.x * uv.x + uv.y * uv.y;
    float theta = atan(uv.y, uv.x);
    float blobSDF = 0.03*sin(iTime)+r + 0.05*cos(iTime)*sin(5.*theta+iTime)+0.1*cos(7.*theta+0.7*iTime) + 1.;
    float smallBlob = smoothstep(1.18, 1.185, mix(blobSDF, r+1.,.8-r));
    float bigBlob = 1.-smoothstep(1.85, 1.9, blobSDF);
    blobSDF = smoothstep(1.42, 1.43, blobSDF);

    float circ1 = sqrt(pow(uv.x - 0.9*sin(0.1*iTime - 2.), 2.) + pow(uv.y - 0.9*cos(0.1*iTime - 2.), 2.));
    float circ2 = sqrt(pow(uv.x - 0.85*sin(0.1*iTime-0.2), 2.) + pow(uv.y - 0.85*cos(0.1*iTime-0.2), 2.));
    float circ3 = sqrt(pow(uv.x - 0.85*sin(0.1*iTime+0.6), 2.) + pow(uv.y - 0.85*cos(0.1*iTime+0.6), 2.));
    float circ4 = sqrt(pow(uv.x - sin(0.1*iTime+0.75), 2.) + pow(uv.y - cos(0.1*iTime+0.75), 2.));
    float circ5 = sqrt(pow(uv.x - 1.05*sin(0.1*iTime-2.3), 2.) + pow(uv.y - 1.05*cos(0.1*iTime-2.3), 2.));
    float circ6 = sqrt(pow(uv.x - 0.64*sin(0.1*iTime-3.1), 2.) + pow(uv.y - 0.64*cos(0.1*iTime-3.1), 2.));
    float circ7 = sqrt(pow(uv.x - 0.59*sin(0.1*iTime-1.), 2.) + pow(uv.y - 0.59*cos(0.1*iTime-1.), 2.));
    blobSDF = min(blobSDF, smoothstep(0.13, 0.14, circ1));
    blobSDF = min(blobSDF, smoothstep(0.07, 0.08, circ2));
    blobSDF = min(blobSDF, smoothstep(0.08, 0.09, circ3));
    blobSDF = min(blobSDF, smoothstep(0.045, 0.05, circ4));
    blobSDF = min(blobSDF, smoothstep(0.045, 0.05, circ5));
    smallBlob = min(smallBlob, smoothstep(0.065, 0.07, circ6));
    smallBlob = min(smallBlob, smoothstep(0.065, 0.07, circ7));

    blobSDF = 1.-blobSDF;
    smallBlob = 1.-smallBlob;
    vec4 albedo = 0.2*vec4(sin(1.03),sin(1.03+radians(120.)),sin(1.03+radians(240.)),1.)+0.8;
    vec4 smallColor = albedo * smallBlob;
    vec4 medColor = vec4(1.*albedo.r, 0.5*albedo.g, 0.6*albedo.b, 1.);
    vec4 bigColor = vec4(0.3*albedo.r, 0.2*albedo.g, 0.6*albedo.b, 1.);
    // Output to screen
    fragColor = mix(mix(medColor*blobSDF, smallColor, smallBlob), bigColor * bigBlob,1.-blobSDF);
    fragColor = blobSDF > 0. && bigBlob < 1. ? bigColor : fragColor;
    fragColor = smallBlob > 0. && blobSDF < 1. ? medColor : fragColor;
}
