float arrow(vec2 uv){
    float c = 0.;
    uv.x = abs(uv.x);
    /*if(uv.y >= 0.){
        c = uv.y + uv.x - 1.;
    } else {
        c = uv.x-.5;
    }/**/
    c = min(max((uv.y - (1.-uv.x))*.5,-uv.y), max((uv.x-.5),abs(uv.y+.5)-.5));
    return smoothstep(0.,.001,c);
}

float arrowPattern(vec2 uv, float scale){
    uv *= scale;
    float c = 0.;
    vec2 id = floor(uv);
    vec2 uf = fract(uv);
    /*if(mod(id.y,2.) == 0.){
        id.x = floor(uv.x+.5);
        uf.x = fract(uv.x+.5);
        if(mod(id.x,2.) == 0.){
            uf.x = 1.-uf.x;
        }
        c = step(uf.y,uf.x);
    } else {
        c = mod(id.x,2.);
    }*/
    c = arrow(uf*2.-1.);
    return c;
}

