#define SLICE_SIZE .05
#define STRENGTH .05

vec3 sdfCircle(vec2 uv, float r) {
    float d = length(uv) - r;
    return vec3(smoothstep(.01, .015, d)) * - 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    vec2 fv = uv;
    fv.x = fract(uv.x / SLICE_SIZE);
    // uv.x += (cos(iTime + fv.x * intensity)) / 5.; // + .5;
    uv.x += tan(iTime * .5 + fv.x * STRENGTH) / 5.;

    vec3 col = vec3(.0);
    float brightness = .85;

    col += sdfCircle(uv, .3) * brightness;
    col -= sdfCircle(uv, .18) * brightness;

    vec3 glass = vec3(abs(fv.x));
    col += glass * .1;

    // vec4 img = texture(iChannel0, uv);
    // col += img.xyz;

    fragColor = vec4(col,1.0);
}
