#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(2,cos(t),1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=2.;
    p=R3D(t*.2);

    for(j=0.;j++<16.;)
        p=abs(p-.5)-.01,
        d=max(max(p.z,p.x-.2),p.y-.2),
        d-=dot(p,p)/1e4;

    e-=d;

}o+=vec4(1.-p,1)*3./i;
}
