float H21(vec2 p){
    vec2 s1 = vec2(cos(p.x * 21.23) + sin(p.y *52.), sin(cos(p.x + 593.1) * p.y));
    return (dot(fract(s1 * 131.52), vec2(123.942, 935.23)));
}
float H21f(vec2 p){
    vec2 s1 = vec2(cos(p.x * 21.23) + sin(p.y *52.), sin(cos(p.x + 593.1) * p.y));
    return fract(dot(fract(s1 * 131.52), vec2(123.942, 935.23)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float zoom = 20.0;
    vec2 luv = fract(uv * zoom) - .5;
    vec2 id = floor(uv * zoom);

    float d = length(luv);
    float r = sin(iTime*1.7 + H21(id))/4. +.25 ;

    float smr = 0.01;
    float mask = smoothstep(r, r - smr, d);

    vec3 col = vec3(1.0, 0.3, 0.4);
    col.r *= H21f(id);
    col *= mask;

    fragColor = vec4(col,1.0);
}
