void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Convert fragment coordinates to UV space
    vec2 uv = fragCoord / iResolution.xy;

    // Define two colors (gradients)
    vec3 colorA = vec3(1.0, 0.0, 1.0); // Black
    vec3 colorB = vec3(0.0, 1.0, 1.0); // White

    // Interpolate between colorA and colorB based on time
    float t = sin(iTime) * 0.5 + 0.5; // Time-based interpolation parameter
    vec3 finalColor = mix(colorA, colorB, t);

    // Output the final color
    fragColor = vec4(finalColor, 1.0);
}
