void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p=uv-vec2(0.33,0.7);
    // Time varying pixel color
    vec3 col = vec3(1.0,0.3,0.0);


    float r=0.2+0.1*cos(atan(p.y,1.0*p.x)*10.0+20.0*p.x+1.0);

    col *=smoothstep(r,r+0.01,length(p));

    r=0.015;
    r +=0.002*cos(120.0*p.y);
    r +=0.000001*exp(-20.0*p.y);
    col*=1.0-(1.0-smoothstep(r,r+0.002,abs(p.x-0.25*sin(2.0*p.y))))*(1.0-smoothstep(0.0,0.01,p.y));

    // Output to screen
    fragColor = vec4(col,1.0);



}
