bool isIn(vec2 z){return length(z)<10.0;}
float pi = 3.14159;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ITERATIONS = 300.0;
    float ZOOM;
    vec2 COORDS;
    if (iMouse.z>0.0){
    ZOOM =10.0;
    COORDS=((2.0*iMouse.xy)-(iResolution.xy))/iResolution.y;
   }else{
    ZOOM =1.0;
    COORDS = vec2(0.0,0.0);
   }

    vec2 uv = COORDS+((4.0/ZOOM)*(fragCoord/iResolution.x))-(vec2(2.0,1.125)/ZOOM);

    vec2 z = uv;
    float i=0.0;
    for (i=0.0;i<ITERATIONS && isIn(z);++i){
    z = vec2((z.x*z.x)-(z.y*z.y),2.0*z.x*z.y) + uv;
    }
    vec3 col = vec3(0.0);
    if (!isIn(z)){
    float time = (iTime+19.25)/5.0;
    i=i-log2(log(length(z)));
    i=i/5.0;
    col = abs(vec3(sin(i+time),sin(i+(time/3.0)),sin(i+(time/2.0))));
    }
    fragColor = vec4(col,1.0);
}
