                            // use ___B for interlaced mode
float AmplitudeA = 0.1;     // Amplitude of wave A
float AmplitudeB = 0.05;     // Amplitude of wave B

float FrequencyA = 10.0;    // frequency of wave A
float FrequencyB = 05.0;    // frequency of wave B 

float TimeMultA = 1.0;      // time multiplier of wave A
float TimeMultB = 1.0;      // time multiplier of wave B 

float OffsetA = 0.0;        // offset of wave A
float OffsetB = 0.0;        // offset of wave B

bool UseInterlacedMode = true;
bool RunShader = true;    


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    if (RunShader) {

        // Interlaced mode
        if (UseInterlacedMode) {

		    if ((int(fragCoord.y) % 4 == 0) || (int(fragCoord.y) % 4 == 1)) {    
                // Even y-coordinate ( trig() )
                uv.x += (sin(uv.y * FrequencyA + (iTime * TimeMultA)) * AmplitudeA) + OffsetA;
            }
            else {
                // Odd y-coordinate ( -trig() )
                uv.x += (-sin(uv.y * FrequencyB + (iTime * TimeMultB)) * AmplitudeB) + OffsetB;
            }
        }
        else {
            uv.x += (sin(uv.y * FrequencyA + (iTime * TimeMultA)) * AmplitudeA) + OffsetA;
        }
    }
    fragColor = texture(iChannel0, uv);
}
