float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}


vec3 circle (vec2 uv, float innerRadius, float outerRadius, vec3 col)
{
    //Makes it in the centre of the fract
    uv -= 0.5;

    //circle sdf
    float d = length(uv);

    //size of inwards circle, subtract before absing
    d-= innerRadius;
    d = abs(d);
    vec3 circle = vec3(d);

    //radius of the outer bound
    float r = outerRadius;
    circle = smoothstep(r,r + 0.01, circle); 
    circle += col;
    return circle;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Does uv set up magic
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 skin = vec3(0.702,0.314,0.000);

    //Fracts it up
    uv.x -= iTime * 1.2; 
    uv.y += sin(iTime * 5.0) / 10.0;
    uv = fract(uv);


    //Makes the sucker
    vec3 suckerCol = vec3(0.780,0.996,1.000);
    vec3 sucker = circle(uv,0.2,0.05, vec3(0.0));

    //Makes the border
    vec3 borderCol = vec3(0.459,0.208,0.000);
    vec3 border = circle(uv,0.265,0.007, vec3(0.0));

    //Mixes the sucker and skin colour using the sucker as a mask
    vec3 suckerAndSkin = mix(suckerCol, skin, sucker);

    //Adds a step to seperate the border from the sucker
    //TO DO make this smooth
    vec3 borderStep = step(sucker, border);

    //Mixes the skin and sucker with the border colour using the border step
    vec3 suckerAndBorder = mix(borderCol, suckerAndSkin, borderStep); 


    fragColor = vec4(suckerAndBorder , 1.0);






}
