// Try setting this to a number like 50, and then taking a long exposure photo of the shader for the duration of one or more complete cycles.
// Setting to 10, you may be able

const int d = 500;
// Set this lower. 10 is decent. 50 is good for timelapse demo


bool jitter = false;
// STROBE WARNING - This greatly increases quality of the image if you enable it by flashing random frames.

void mainImage( out vec4 O, vec2 U )
{
    int s = (int(U.x + U.y) + iFrame)%3;
    vec2 r = iResolution.xy;
    vec4 tex = texture(iChannel0, U/r);

    // 87 should be a prime number. Any prime works.
    int t = d - (iFrame*(jitter?87:1))%d - 1;
    int x = int(tex.x * float(d));
    int y = int(tex.y * float(d));
    int z = int(tex.z * float(d));


    O = vec4(0);

    switch(s){
        case 0:
            O.x = float(x>t);
            break;
        case 1:
            O.y = float(y>t);
            break;
        case 2:
            O.z = float(z>t);
            break;
    }
}
