void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sc = 1.+0.5*sin(iTime);
    vec2 uv = sc*(2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 uvMouse = sc*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);
    float maxI = 1000.;
    vec2 C = uv;
    float escapeTime;
    for(float i = 0.; i<maxI; i++)
    {
        C = mat2(C,-C.y,C.x) * C + uvMouse.xy;       
        if(length(C)>2.0)
        {
            escapeTime = 2.*i/maxI; 
            break;
        }
    }
    col = vec3(escapeTime,10.0*length(C)/maxI,1.0+atan(C.x/C.y));

    fragColor = vec4(sin(col*10.0),1.0);
}
