const float SPEED = 2.0f;
const float FREQ = 12.0f;
const float HEIGHT = 0.2f;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Sky gradient
    vec3 bottomCol = vec3(0.1,0.4,0.4);
    vec3 topCol = vec3(0.1,0.1,0.15);
    vec3 col = mix(bottomCol,topCol,pow(uv.y,0.5));
    float star = smoothstep(0.96,0.98,texture(iChannel0,uv).r)*0.8*pow(uv.y,2.0);
    star*=texture(iChannel0,uv * 0.2 + iTime * 0.01).r;
    col+=star;

    //Moon
    vec2 circleCenter = vec2(0.0,0.2);
    vec2 circleUV = uv * 2.0 - 1.0;
    circleUV.x*=iResolution.x/iResolution.y;
    float circleD = distance(circleUV,circleCenter);
    vec3 moonCol = vec3(0.8,0.9,1.0);
    moonCol-=texture(iChannel1,uv).r*0.4;
    col = mix(moonCol,col,smoothstep(0.1,0.11,circleD));
    //Moon glow
    col += vec3(0.8,0.9,1.0) * pow(clamp(1.0-circleD,0.0,1.0),2.0) * 0.6;

    //Clouds
    vec2 cloudUV = uv + texture(iChannel0,uv*0.1).rg*0.04 + vec2(iTime*0.01,0.0);
    cloudUV*=0.1;
    float cloudAmnt = smoothstep(0.35,0.65,texture(iChannel1,cloudUV).r);
    cloudAmnt*=0.8;
    cloudAmnt = clamp(0.0,1.0,cloudAmnt);
    vec3 cloudCol = mix(vec3(0.1,0.23,0.26),vec3(0.1),smoothstep(0.5,2.0,circleD));
    col=mix(col,cloudCol,cloudAmnt);

    //Ghosts
    float h = abs(sin(uv.x*FREQ+iTime * SPEED)+sin(uv.x*FREQ*2.0-iTime * SPEED)*0.25);
    h*=HEIGHT;
    h-=0.05;
    float py = 1.0/iResolution.y;
    float t = smoothstep(h,h+py,uv.y);
    vec3 rim = vec3(0.8,0.9,1.0) * clamp(pow(uv.y/h,2.0),0.0,1.0) * 0.7;
    vec3 ghostCol = mix(vec3(0.9),vec3(1.0),uv.y/h)*0.5;
    ghostCol+=rim;
    col = mix(ghostCol,col,t);

    //Vignette
    col*=pow(clamp(1.0-distance(uv,vec2(0.5)),0.0,1.0),0.3);

    // Output to screen
    fragColor = vec4(col,1.0);

}
