vec3 blackbody(float temp) {
    //Remap [1000k, 10000k] to [0, 1] range
    //temp = (1.0/9000.0) * temp - (1.0/9.0);

    float s0 = max(temp - 0.1741, 0.0) / (temp - 0.1741);
    float s1 = max(temp - 0.5871, 0.0) / (temp - 0.5871);

    float Ra = 0.157673638094 * temp + 0.972549019608;
    float Rb = -0.503374030649 * temp + 1.29553089339;
    float Lr = min(1.0, mix(Ra, Rb, s0));

    float Ga = 2.74802626392 * temp + 0.149019607843;
    float Gb = 0.902055737549 * temp + 0.470403076485;
    float Gc = -0.389402552011 * temp + 1.22861823829;
    float Lg = mix(mix(Ga, Gb, s0), Gc, s1);

    float Ba = 2.42130750605 * temp - 0.421549636804;
    float Lb = clamp(Ba, 0.0, 1.0);

    return vec3(Lr, Lg, Lb);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec3 bb = blackbody(uv.x);

    // Output to screen
    fragColor = vec4(pow(bb, vec3(2.2)),1.0);
}
