vec3 mainfunction(vec2 uv , vec3 scattered[5], vec3 color[5]){


    vec3 ourpoint = vec3(uv.x,uv.y,0);
    vec3 si[5];
    float ri[5];
    for(int i=0;i<5;i++){
        si[i] = scattered[i] - ourpoint;
        ri[i] = length(si[i]);
    }

    float area[4];
    float di[4];

    float radian = acos(dot(normalize(si[1]),normalize(si[2])))+acos(dot(normalize(si[3]),normalize(si[1])))+acos(dot(normalize(si[2]),normalize(si[3])));
    if(radian <= 3.1415 * 2.0f){return vec3(0);}

    for(int i=0;i<4;i++){
        area[i] = length(cross(si[i],si[i+1]))/2.0f;
        di[i] = dot(si[i],si[i+1]);
        if(ri[i]<=0.0015f){
            return color[i];
        }
        if(area[i]<=0.0003f && di[i]<=0.00001f){
            return (ri[i+1]*color[i]+ri[i]*color[i+1]) / (ri[i+1] + ri[i]);
        }
    }

    vec3 F;
    float W;

    for(int i=1;i<=3;i++){
        float w;
        if(area[i-1]!=0.0f){
            w = w + (ri[i-1]-di[i-1]/ri[i])/area[i-1];
        }
        if(area[i]!=0.0f){
            w = w + (ri[i+1]-di[i]/ri[i])/area[i];
        }
        F = F + w * color[i];
        W = W + w;
    }
    return F/W;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 red = vec3(1,0,0);
    vec3 orange = vec3(0.8,0.5,0);
    vec3 azure = vec3(0,0.1,1);
    vec3 purple = vec3(1,0,0.8);
    vec3 green = vec3(0,1,0);
    vec3 yellow = vec3(0.8,1,0);


    vec3 leftbottom = vec3(0.1,0.1,0);
    vec3 lefttop = vec3(0.1,0.9,0);
    vec3 midtop = vec3(0.5,0.9,0);
    vec3 midbottom = vec3(0.5,0.1,0);   


    vec3 p1 = leftbottom;
    vec3 p2 = midtop;
    vec3 pa = midbottom;
    vec3 pb = lefttop;    

    /*
    vec3 p1 = vec3(0.25,    0.0,    0);
    vec3 p2 = vec3(0.85,     0.75,    0);
    vec3 pa = vec3(0.85,    0.19,     0);
    vec3 pb = vec3(0.65,    0.99,     0);
    */

    vec3 receive = mainfunction(fragCoord/iResolution.xy , 
    vec3[5](
        p1,
        p2,
        pa,
        p1,
        p2
    ),
    //color
    vec3[5](
        red,
        green,
        azure,
        red,
        green
    )
    );

    receive += mainfunction(fragCoord/iResolution.xy , 
    vec3[5](
        p1,
        p2,
        pb,
        p1,
        p2
    ),
    //color
    vec3[5](
        red,
        green,
        yellow,
        red,
        green
    )
    );

    // Output to screen
    fragColor = vec4(receive,1.0f);
}
