float pi = 3.14159265359;

vec3 star(vec2 uv, float intensity) {
    float f = length(uv);

    vec3 color = vec3(0.8, 1., 0.5);    
    vec3 col = exp(-color * f * 15. + intensity);
    return col;
}

vec3 jets(vec2 uv, float speed, float thickness) {
    float a = atan(uv.y, uv.x);
    float r = length(uv);
    vec2 puv = vec2(a, r);
    float w = 0.125 + thickness * sin(speed * iTime + pi);

    float f = .5 + .5 *( cos(puv.x*2. + 0.8 * cos(iTime * speed) + pi)-(1.-w) )/w;
    f = clamp(f, 0., 1.);
    f *= 2. * r;
    vec3 col = pow(vec3(f), 2.*vec3(0.8, 1., 0.5)) ;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = jets(uv, 14., 0.03) + star(uv, 0.75);

    fragColor = vec4(col, 1);


}
