void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f / iResolution.x;
    float t = iTime * 0.1;
    float yy = exp(uv.y * 6.),
        k = floor(yy)*3.0,

        x = fract(uv.x*k*(iResolution.x/iResolution.y));
    vec2 u = vec2(fract(x + iTime * 0.5 * 1./k), fract(yy));
    vec3 blue = vec3(0., 120., 191.) / 255.;
    //O = vec4(mix(blue, vec3(1.), d),1.);
    O = texture(iChannel0,u);
}
