void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = uv - vec2(0.33, 0.7);

    vec3 sunset = mix( vec3(1.0, 0.3, 0.0),  vec3(1.0, .8,.3), sqrt(uv.y));

    vec3 col = mix( vec3(0.15,0.15,0.15), sunset, uv.y * uv.x +sin(0.5 *iTime));


    float r = 0.2 + 0.1 * cos( atan(center.y,center.x) * 10.0 + 20.0*center.x + 1.0 + .5*sin(iTime));
    col *= smoothstep(r,r +0.01,length(center));

    r = 0.015;
    r+= 0.002*cos(120.0 * center.y);
    r += exp(-70.0 * uv.y);
    col *= 1.0 - (1.0 -smoothstep(r, r+0.002, abs(center.x - 0.2* sin(2.0 * center.y)))) * (1.0- smoothstep(0.0, 0.1, center.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
