#define PI acos(-1.0)

float conicalGradient(vec2 uv, vec2 center) {
    vec2 offset = uv - center;
    return atan(-offset.y, -offset.x) / (2.0 * PI) + 0.5;
}


vec3 conicalColor (vec2 uv)
{
    vec2 center = vec2(0,0);
    uv -= 0.5;
    uv *= 2.0;

    //fix for aspect ratio.
    uv.x *= iResolution.x / iResolution.y;
    float conical = conicalGradient(uv, center);

    vec3 col = vec3(1.0-conical,0,0);
    return col;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //vec2 tileduv = tile(uv);

    vec3 color = conicalColor(uv);


    // Output to screen
    fragColor = vec4(color,1.0);
}
