void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 startColor = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 endColor = vec4(0.1, 0.1, 0.1, 1.0);

    vec2 point = vec2(0.5, 0.5);

    vec2 uv = fragCoord/iResolution.xy;
    uv.x += sin(uv.y*10.0+iTime*4.0)/20.0;

    vec2 relative = uv - point;

    float angle = atan(relative.x, relative.y);
    float t = (angle + 3.14) / 2.0 / 3.14;

    fragColor = mix(startColor, endColor, t);
}
