const vec2 FULCRUM = vec2(0., -1.5);
const float RADIUS = 1.8;
const float STRING = .03;
const float COIN_OUT = .4;
const float COIN_IN = -.06;

const float TURN = acos(-1.) * 2.;
// rotation matrix
#define ROT(x) mat2x2(cos(x + TURN * vec4(0, 0.25, -0.25, 0)))
// fade function defined by ken perlin
//float fade(float t) {
//  return t * t * t * (t * (t * 6. - 15.) + 10.);
//}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 2.5);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    uv.y *= -1.;

    vec2 uvPend = ((uv + FULCRUM) * ROT(0.3 * cos(t * TURN))) + vec2(0, RADIUS);

    float r = length(uvPend);
    float a = atan(uvPend.y, uvPend.x) / TURN;

    vec3 col = mix(
        mix(
            mix(
                vec3(0.7,0.2,0.5),
                vec3(0.5,0.1,0.4),
                step(fract(
                    2.0 * log(r) +
                    5. * a +
                    -0.2 * cos(-0.3 + t * TURN)
                    //+1. * t
                ), 0.5)
            ),
            // string color
            vec3(0.8),
            // string
            step(abs(uvPend.x), STRING)
            * step(0.9 * COIN_OUT, uvPend.y)
        ),
        // coin color
        mix(
            vec3(0.98, 0.9, 0.92),
            vec3(0.9, 0.5, 0.6),
            step(fract(
                5.5 * r +
                2. * a
            ), 0.5)
        ),
        // coin
        step(r, COIN_OUT) * step(COIN_IN, r)
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
