#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

#define pi 3.1415926
#define tau (pi+pi)

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float ring(vec3 p, vec3 n, float r) // https://www.shadertoy.com/view/MffcWr oriented ring (jt)
{
    n = normalize(n);
    float z = dot(p,n);
    return length(vec2(length(p - n*z)-r,z));
}

float angle(vec2 p, vec2 c) // based on https://iquilezles.org/articles/distfunctions/
{
    c = normalize(c);
    vec2 q = vec2(abs(p.x), p.y);
    return length(q-c*max(dot(q,c),0.0))*((q.x*c.y-q.y*c.x<0.0)?-1.0:1.0);
}

float dot2(vec3 v)
{
    return dot(v,v);
}

// returns vector from sphere s0 to center of circle and circle radius
vec4 sphere_sphere_intersection(vec4 a, vec4 b) // https://www.shadertoy.com/view/M3lBRS sphere sphere intersection (jt)
{
    float c = dot2(b.xyz-a.xyz);
    float e = a.w*a.w;
    float f = b.w*b.w;
    return
        vec4
        (
            (b.xyz-a.xyz)*(c-f+e)/c,
            sqrt((-c*c+2.0*c*e+2.0*c*f-e*e-f*f+2.0*e*f)/c)
        )
        /
        2.0;
}

vec2 project_on_line(vec3 p, vec3 d)
{
    return vec2(length(p-d*dot(p,d)/dot2(d)),dot(p,d)/length(d));
}

float sphere_sphere_intersection(vec3 p, vec4 s0, vec4 s1) // https://www.shadertoy.com/view/MXlfRB sphere intersection construction
{
    vec4 q = sphere_sphere_intersection(s0, s1);
    float a0 = +angle(project_on_line(p-s0.xyz,q.xyz),vec2(q.w,+length(q.xyz)));
    float a1 = -angle(project_on_line(p-s1.xyz,q.xyz),vec2(q.w,+length(q.xyz)-distance(s0.xyz,s1.xyz)));
    return
        a0 > 0.0 && a1 > 0.0
        ?
        ring(p-s0.xyz-q.xyz,normalize(q.xyz),q.w)
        :
        max(sphere(p-s0.xyz, s0.w),sphere(p-s1.xyz,s1.w));
}

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec4 intersect(vec4 a, vec4 b)
{
    return a.w > b.w ? a : b;
}

vec4 map(vec3 p)
{
    float t = tau*iTime/10.0;
    vec4 s0 = vec4(-0.7-cos(t)*0.1,0.0,0.0,+1.25);
    vec4 s1 = vec4(+0.7+cos(t)*0.1,0.0,0.5,+0.5);
    //vec4 s0 = vec4(0.0,0.0,sin(t/10.0)*0.5,+1.0);
    //vec4 s1 = vec4(cos(t/10.0)*1.5,0.0,0.0,+0.5);

    vec4 q = sphere_sphere_intersection(s0, s1);

    float d0 = abs(sphere(p-s0.xyz, s0.w));
    float d1 = abs(sphere(p-s1.xyz, s1.w));

    float a0 = angle(project_on_line(p-s0.xyz,q.xyz),vec2(q.w,+length(q.xyz)));
    float a1 = angle(project_on_line(p-s1.xyz,q.xyz),vec2(q.w,+length(q.xyz)-distance(s0.xyz,s1.xyz)));
//return vec4(vec3(1),sphere_sphere_intersection(p,s0,s1));
    return
        combine
        (
            intersect // not exact, just a quick hack to make visualization more interesting
            (
                combine
                (
                    combine
                    (
                        combine
                        (
                            vec4(vec3(0,0,1), d0-0.01),
                            vec4(vec3(0,1,0), d1-0.01)
                        ),
                        distance(s0.xyz,s1.xyz) < s0.w+s1.w
                        &&
                        distance(s0.xyz,s1.xyz) > abs(s0.w-s1.w)
                        ?
                        vec4(vec3(1,0,0), ring(p-s0.xyz-q.xyz,normalize(q.xyz),q.w)-0.05)
                        :
                        vec4(vec3(0), DIST_MAX) // no intersection
                    ),
                    combine
                    (
                        vec4(vec3(0.25),abs(a0)-0.01),
                        vec4(vec3(0.25),abs(a1)-0.01)
                    )
                ),
                vec4(vec3(1),p.y)
            ),
            vec4(vec3(1,0.5,0),sphere_sphere_intersection(p,s0,s1))
        );
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return
        normalize
        (
            k.xyy * map(p + k.xyy*h).w
            +
            k.yyx * map(p + k.yyx*h).w
            +
            k.yxy * map(p + k.yxy*h).w
            +
            k.xxx * map(p + k.xxx*h).w
        );
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1) // pass on running out of iterations
{
    // NOTE: Limited number of iterations to avoid stalling
    //       when ray passes closely (just above EPSILON)
    //       in parallel to a surface.
    uint i;
    vec4 h = vec4(0);
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        h = map(ro + rd * t);
        if(h.w < EPSILON)
            return vec4(h.xyz,t);
        t += h.w;
    }

    return vec4(h.xyz,t); // stop on running out of iterations
    //return vec4(0,0,0,t1); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).w;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

void mainImage(out vec4 o, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (I + I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = tau * float(iMouse.x) / float(R.x);
    float pitch = pi - pi * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : sin(tau * fract(iTime * 0.02))*pi/3.0; // demo: oscillate front view
    pitch = !demo ? pitch :  pi / 2.0;

    vec3 ro = vec3(0,0,-3);
    vec3 rd = normalize(vec3(I.xy, 2.5)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;

    vec3 lightdir = normalize(vec3(0,1,1));

    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = result.xyz;
    float dist = result.w;
    if(dist < DIST_MAX)
    {
        vec3 dst = ro + rd * dist;
        vec3 n = normal(dst);

        vec3 ambient = vec3(0.05);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 25.0);
        color *= (ambient * calcAO(dst, n)) + brightness * 4.0;

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        vec3 fog_color = vec3(0);
        color = mix(fog_color, vec3(color), exp(-pow(dist/20.0, 2.0))); // fog
    }
    else
    {
        color *= 0.0;
    }

    color = tanh(color); // roll-off overly bright colors
    color = sqrt(color); // approximate gamma

    //color += hash32(I*123.0)/255.0; // dither to remove banding in the background

    o = vec4(color, 1);
}
