void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = I/iR.xy;

    float amount = hash21(vec2(uv.y, iTime)) - .5;
    amount *= (iM.z > 0.) ? (iM.x / iR.x) / 10. : .1;

    uv = vec2((uv.x + amount), uv.y);

    vec3 col = texture(iChannel0, uv).rgb;

    O = vec4(col, 1.);

}

#define iM iMouse
#define iR iResolution

float hash21(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
