const float pi = acos(-1.);
const float tau = pi*2.;
float rand(vec3 p){
    return sin(p.x*66.36+p.y*87.83+p.z*54.82)*634.09;
}
mat2 rot(float a){
    return mat2(vec2(cos(a),-sin(a)),vec2(sin(a),cos(a)));
}
float pacman(vec2 p, float t) {
    float px = 8./iResolution.y;
    p *= rot(t*tau/2.);
    return smoothstep(-px,0.,length(p)-.5)+smoothstep(-px,0.,min(p.x,p.y)*2.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv *= vec2(iResolution.x/iResolution.y,1.);

    vec2 i_uv = floor(uv*4.),
         f_uv = fract(uv*4.);
         f_uv = f_uv-.5;

    vec2 ang = vec2(rand(vec3(i_uv,floor(iTime))),
                    rand(vec3(i_uv,floor(iTime)+1.)));
    ang = mod(floor(ang),4.);
    float val = pacman(f_uv, mix(ang.x,ang.y,sin(fract(iTime)*pi-pi/2.)*.5+.5));

    fragColor = vec4(vec3(val),1.0);
}
