void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/(iResolution.xy);

    // Sample and filter b/w strengthmap of image
    vec2 b_uv = round(uv * 16.0) / 16.0;
    vec2 tex_uv = mix(b_uv, uv, 0.5);
    vec4 color = texture(iChannel0, tex_uv).rgba;

    // Output to screen
    fragColor = color;
}
