#define LINE_OPACITY 0.5
#define LINE_THICKNESS 2.
#define LINE_GAP 2
#define COLOR black

// list of colors
vec4 black;
vec4 white = vec4(1.);
vec4 red = vec4(1.,0.2,0.2,1.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = vec2(fragCoord.x / iResolution.x,fragCoord.y / iResolution.y);

    vec4 tex = texture(iChannel0, uv);

    // LINES
    float lines;
    if (int(floor(fragCoord.y / LINE_THICKNESS)) % LINE_GAP == 0) {
        lines = LINE_OPACITY;
    }
    else { lines = .0; }

    // RENDER
    vec4 col;

    col = tex;
    col = mix(col, COLOR, lines);
    fragColor = col;
}
