void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float yDisp = sin(iTime) * 0.2 + 0.1;
    float periodDisp = sin(iTime * 0.3352);

    float col = smoothstep(0.0, 0.4 + yDisp, 1.0 - uv.y + sin(2.0 * 3.14 * uv.x * periodDisp + iTime) * 0.05);


    float contrast = 0.1; //exposure
    float brightness = 0.5; //set according to contrast
    float zoom = 0.6;
    float messiness = 1.0; //1.0 curtainy, 5.0 silky
    float fall = 0.3;
    float side = 0.1;
    float chunks = 4.0; // 20, but adjust according to scale
    float wave = 0.03; // 0.02
    float waveRepeat = 20.0; // 8.0

    vec2 uvNew = uv/zoom;
    //uv *= rotationMatrix(45.0);

    float t = 0.3*iTime;
    float tsin = 0.5 * sin(iTime/2.0);

	uv.y += wave*sin(waveRepeat*uv.x-t);
    float f = brightness + contrast*sin(messiness*(uv.x+uv.y+cos(3.0*uv.x+5.0*uv.y)+fall*t)+sin(chunks*(uv.x+uv.y-side*tsin)));

    float displacement = f - 0.5;

    uv.x += displacement;

    vec3 pic = texture(iChannel0, uv).rgb;

    fragColor = vec4(pic * col, 1.0);
}
